mapper.select:
select *
from config_ad

    1=1

        and ad_id = #{queryParams.adId}


        and ad_type = #{queryParams.adType}


        and ad_source_type = #{queryParams.adSourceType}


        and ad_source_url = #{queryParams.adSourceUrl}


        and ad_link = #{queryParams.adLink}


        and status = #{queryParams.status}


        and create_time = #{queryParams.createTime}


        and update_time = #{queryParams.updateTime}
