mapper.select:
select *
from config_house

    1=1

        and house_id = #{queryParams.houseId}


        and house_name = #{queryParams.houseName}


        and address = #{queryParams.address}


        and gps_lat_lon = #{queryParams.gpsLatLon}


        and live_streaming_link = #{queryParams.liveStreamingLink}


        and create_time = #{queryParams.createTime}


        and update_time = #{queryParams.updateTime}
