mapper.select:
select *
from sys_user

    1=1

        and user_id = #{queryParams.userId}


        and wechat_account = #{queryParams.wechatAccount}


        and wechat_name = #{queryParams.wechatName}


        and wechat_avatar = #{queryParams.wechatAvatar}


        and open_id = #{queryParams.openId}


        and phone_number = #{queryParams.phoneNumber}


        and last_login_time = #{queryParams.lastLoginTime}


        and status = #{queryParams.status}


        and create_time = #{queryParams.createTime}


        and update_time = #{queryParams.updateTime}
